package Statement;

public class NewForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int a=1; a<=10; a++) {     // for loop 
			 				
			System.out.println("hello");
			}
		for (long b=1 ; b<=10; b++) {    // for loop with break
			if(b==5) {
				break;
			}
			System.out.println("Hi");
		}
		for (short c=2; c<=6; c++) {   // for loop with continue
			if(c==5) {
				continue;
			}
			System.out.println("bytesquare");
		}
		}

	}


