package Statement;

public class WhileLoopStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   int a=15;
   while(a>=0) {
	  	 
	   System.out.println(a);
	   a--;
	   
   }
   
  while (a<=15) {
	   System.out.println(a);
	   a++;
	   
	   
	 
   }
	
	}
}

